package com.example.book.QuanLy.manage;import android.os.Bundle;import android.widget.ArrayAdapter;import android.widget.ListView;import androidx.appcompat.app.AppCompatActivity;import com.example.book.R;public class AddBlacklistActivity extends AppCompatActivity {    String list[] = {"Peter Parker","Joe","Adam Levine"};    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.manage_add_blacklist_layout);        setTitle("Thêm danh sách đen");        ListView listView = findViewById(R.id.listAddBlacklist);        ArrayAdapter<String> adapter = new ArrayAdapter<String>(this,R.layout.manage_add_blacklist_items,R.id.txtAddBlacklist,list);        listView.setAdapter(adapter);    }}