package com.example.book.QuanLy.manage;import android.os.Bundle;import android.widget.ArrayAdapter;import android.widget.ListView;import androidx.appcompat.app.AppCompatActivity;import com.example.book.R;public class BlockActivity extends AppCompatActivity {    String list[] = {"Peter Parker","Joe","Adam Levine"};    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.manage_block_layout);        setTitle("Danh Sách Chặn");        ListView listView = findViewById(R.id.listBlock);        ArrayAdapter<String> adapter = new ArrayAdapter<String>(this, R.layout.manage_block_items, R.id.txtBlock,list);        listView.setAdapter(adapter);    }}