package com.example.book.QuanLy.manage;import android.os.Bundle;import android.widget.ArrayAdapter;import android.widget.ListView;import android.widget.Spinner;import androidx.appcompat.app.AppCompatActivity;import com.example.book.R;public class EmployeeActivity extends AppCompatActivity {    String list[] = {"Peter Parker","Joe","Adam Levine"};    String listspinner[] = {"Quản lý","Nhân viên giao hàng","Nhân viên xử lý đơn","Nhân viên đóng gói","Chủ cửa hàng"};    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.manage_employee_layout);        setTitle("Quản lý nhân viên");        ListView listView = findViewById(R.id.listEmployee);        Spinner spinner=findViewById(R.id.spinerEmployee);        ArrayAdapter<String> spinnerAdapter = new ArrayAdapter<String>(this,android.R.layout.simple_spinner_item,listspinner);        spinner.setAdapter(spinnerAdapter);        ArrayAdapter<String> adapter = new ArrayAdapter<String>(this,R.layout.manage_employee_items,R.id.txtEmployee,list);        listView.setAdapter(adapter);    }}