package com.example.book.QuanLy.manage;import android.os.Bundle;import android.view.View;import android.widget.AdapterView;import android.widget.ListView;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.appcompat.app.AppCompatActivity;import com.example.book.QuanLy.adapter.MessageAdapter;import com.example.book.QuanLy.models.Message;import com.example.book.QuanLy.models.MessageDetail;import com.example.book.R;import com.example.book.SoanDon.models.User;import com.google.firebase.database.ChildEventListener;import com.google.firebase.database.DataSnapshot;import com.google.firebase.database.DatabaseError;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;import java.util.ArrayList;public class MessageActivity extends AppCompatActivity {    private MessageAdapter adapter;    private ArrayList<Message> messList = new ArrayList<Message>();    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.manage_message_layout);        setTitle("Quản lý tin nhắn");        ListView listView = findViewById(R.id.listmessage);        adapter = new MessageAdapter(this, R.layout.manage_message_items, messList);        listView.setAdapter(adapter);        DatabaseReference mDatabase = FirebaseDatabase.getInstance().getReference("messages");        mDatabase.addChildEventListener(new ChildEventListener() {            @Override            public void onChildAdded(@NonNull DataSnapshot snapshot, @Nullable String previousChildName) {                messList.add(new Message(snapshot.getKey(), "", "9:00 PM"));                adapter.notifyDataSetChanged();            }            @Override            public void onChildChanged(@NonNull DataSnapshot snapshot, @Nullable String previousChildName) {            }            @Override            public void onChildRemoved(@NonNull DataSnapshot snapshot) {            }            @Override            public void onChildMoved(@NonNull DataSnapshot snapshot, @Nullable String previousChildName) {            }            @Override            public void onCancelled(@NonNull DatabaseError error) {            }        });        listView.setOnItemClickListener(new AdapterView.OnItemClickListener() {            @Override            public void onItemClick(AdapterView<?> parent, View view, int position, long id) {                            }        });    }}