package com.example.book.QuanLy.manage;import android.os.Bundle;import android.widget.ListView;import androidx.appcompat.app.AppCompatActivity;import com.example.book.R;import com.example.book.QuanLy.adapter.MessageAdapter;import com.example.book.QuanLy.models.Message;import java.util.ArrayList;public class MessageActivity extends AppCompatActivity {    private MessageAdapter adapter;    private ArrayList<Message> messList=new ArrayList<Message>();    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.manage_message_layout);        setTitle("Quản lý tin nhắn");        ListView listView = findViewById(R.id.listmessage);        Message mess1=new Message("Peter Parter","Xin chao shop, cho em hoi...","8:00 CH");        Message mess2=new Message("Joe Parter","Hello, cho em hoi...","9:00 CH");        messList.add(mess1);        messList.add(mess2);        adapter = new MessageAdapter(this,R.layout.manage_message_items,messList);        listView.setAdapter(adapter);    }}