package com.example.book.QuanLy.manage;import android.os.Bundle;import android.widget.ArrayAdapter;import android.widget.ListView;import androidx.appcompat.app.AppCompatActivity;import com.example.book.R;public class UserActivity extends AppCompatActivity {    String list[] = {"Peter Parker","Joe","Adam Levine"};    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.manage_user_layout);        setTitle("Quản lý người dùng");        ListView listView = findViewById(R.id.listUser);        ArrayAdapter<String> adapter = new ArrayAdapter<String>(this,R.layout.manage_user_items,R.id.txtUser,list);        listView.setAdapter(adapter);    }}