package com.example.book.QuanLy.models;public class Message {    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getContent() {        return content;    }    public void setContent(String content) {        this.content = content;    }    public String getTime() {        return time;    }    public void setTime(String time) {        this.time = time;    }    String name;    String content;    String time;    String id;    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public Message(String name, String content, String time, String id) {        this.name = name;        this.content = content;        this.time = time;        this.id = id;    }}