package com.example.book.SoanDon;import android.os.Bundle;import android.widget.ListView;import androidx.appcompat.app.AppCompatActivity;import com.example.book.R;import com.example.book.SoanDon.adapter.DeliveredAdapter;import com.example.book.SoanDon.models.Delivered;import java.util.ArrayList;public class DeliveredActivity extends AppCompatActivity {    private DeliveredAdapter deliveredAdapter;    private ArrayList<Delivered> list=new ArrayList<>();    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.packager_delivered_layout);        ListView listView = findViewById(R.id.listDelivered);        setTitle("Đã giao hàng");        Delivered d =new Delivered("Đơn hàng 1","120000đ");        Delivered d2 =new Delivered("Đơn hàng 2","100000đ");        list.add(d);        list.add(d2);        deliveredAdapter = new DeliveredAdapter(this,R.layout.packager_delivered_items,list);        listView.setAdapter(deliveredAdapter);    }}