package com.example.book.SoanDon;import android.os.Bundle;import android.widget.ListView;import androidx.appcompat.app.AppCompatActivity;import com.example.book.R;import com.example.book.SoanDon.adapter.OrderAdapter;import com.example.book.SoanDon.models.Book;import java.util.ArrayList;public class OrderDetailActivity extends AppCompatActivity {    private OrderAdapter adapter;    private ArrayList<Book> books=new ArrayList<>();    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.packager_order_detail_layout);        setTitle("Chi tiết hoá đơn");        ListView listView = findViewById(R.id.listOrder);        Book book=new Book("Hai so phan","90000vnd","2");        Book book2=new Book("Mat biec","30000vnd","1");        books.add(book);        books.add(book2);        adapter = new OrderAdapter(this,R.layout.packager_order_items,books);        listView.setAdapter(adapter);    }}