package com.example.book.SoanDon;import android.os.Bundle;import android.widget.ArrayAdapter;import android.widget.ListView;import android.widget.Spinner;import androidx.appcompat.app.AppCompatActivity;import com.example.book.R;import com.example.book.SoanDon.adapter.StorehouseAdapter;import com.example.book.SoanDon.models.Storehouse;import java.util.ArrayList;public class StorehouseActivity extends AppCompatActivity {    String check[] = {"Chờ xác nhận","Đã xác nhận"};    private StorehouseAdapter storehouseAdapter;    private ArrayList<Storehouse> storehousesList=new ArrayList<>();    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.packager_storehouse_layout);        setTitle("Kho hàng");        ListView listView = findViewById(R.id.listStorehouse);        ArrayAdapter<String> adapter = new ArrayAdapter<String>(this, android.R.layout.simple_spinner_item,check);        adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);        Storehouse storehouse=new Storehouse("Hai so phan","2");        Storehouse storehouse1=new Storehouse("Mat biec","12");        storehousesList.add(storehouse);        storehousesList.add(storehouse1);        storehouseAdapter = new StorehouseAdapter(this,R.layout.packager_storehouse_items,storehousesList);        listView.setAdapter(storehouseAdapter);    }}